void game_cycle(void)
{
    unsigned int k;
    boolean ve,pa;
    while(1){
        current_position=next_position;
        if(marker[120]==false&&(marker[121]==true||marker[122]==true)) {
            writeln("");
            evidence1();
            show_messagenlf(world[search_room(current_position)].short_d);
            writesameln("  ");
            normaltxt();
            show_message(world[search_room(current_position)].long_d);
            writeln("");
            marker[120]=true;
            ve=false;
            for(k=0;k<OSIZE;++k)
                if(obj[k].position==current_position) {
                    if(ve==false) {
                        show_message(message1031);
                        ve=true;
                        evidence2();
                    }
                    show_message(obj[k].desc);
                }
            normaltxt();
            if(marker[124]==true) {
                pa=false;
                for(k=0; k<6; ++k)
                    if(world[search_room(current_position)].directions[k]!=0) {
                        if(pa==false) {
                            show_messagenlf(message1020);
                            pa=true;
                        }
                    evidence3();
                    show_messagenlf(dir[k]);
                    writesameln(" ");
                }
                normaltxt();
                writeln("");
            }
        }
        ++counter[125];
        --counter[126];
        --counter[127];
        --counter[128];
        if(hi_cond()!=0) continue;
        writeln("");
        if(ls==0) show_message(message1012);
        interrogationAndAnalysis(DSIZE);
        if(local_cond()!=0) continue;
        if(low_cond()!=0) continue;
        show_message(verb==0?message1009:message1010);
    }
}

int main(void)
{
    init_term();
    greetings();
    next_position=8;
    marker[124]=true;
    marker[121]=true;
    counter[121]=10000;
    counter[122]=10000;
    game_cycle();
    return 0;
}
